    filterButton(cat);
  }
  return (
    <Row className="my-2 mb-5  ">
    <Col className='d-flex justify-content-center' >
    <Button onClick={()=>items.filter('فطار')} className="btn mx-2" style={{background:'#B45B35',color:'black'}} >BreackFast</Button>
    <Button onClick={()=>items.filter('غداء')}  className="btn mx-2" style={{background:'#B45B35',color:'black'}} >Dinner</Button>
    <Button  onClick={()=>items.filter('عشاء')}  className="btn mx-2" style={{background:'#B45B35',color:'black'}}>3a4a</Button>
    <Button onClick={()=>items.filter('فطار')}  className="btn mx-2" style={{background:'#B45B35',color:'black'}}>All</Button>
    </Col>
    

    </Row>
  )
}

export default Buttons
